import 'package:flutter/material.dart';

void main() {
  runApp(MyApp());
}

// Classe para representar um atleta
class Athlete {
  final String name; // Nome do atleta
  final String image; // URL da imagem do atleta
  int rank; // Classificação do atleta

  // Construtor
  Athlete({required this.name, required this.image, required this.rank});
}

// Classe principal do aplicativo
class MyApp extends StatelessWidget {
  // Lista de atletas
  final List<Athlete> athletes = [
    Athlete(name: 'Cristiano Ronaldo', image: 'https://upload.wikimedia.org/wikipedia/commons/thumb/d/d7/Cristiano_Ronaldo_playing_for_Al_Nassr_FC_against_Persepolis%2C_September_2023_%28cropped%29.jpg/360px-Cristiano_Ronaldo_playing_for_Al_Nassr_FC_against_Persepolis%2C_September_2023_%28cropped%29.jpg', rank: 1),
    Athlete(name: 'Messi', image: 'data:image/jpeg;base64,', rank: 2),
    Athlete(name: 'Pelé', image: 'https://upload.wikimedia.org/wikipedia/commons/5/5e/Pele_con_brasil_%28cropped%29.jpg', rank: 3),
    Athlete(name: 'Michael Phelps', image: 'data:image/jpeg;base64,', rank: 4),
    Athlete(name: 'Usain Bolt', image: 'data:image/jpeg;base64,', rank: 5),
    Athlete(name: 'Michael Jordan', image: 'data:image/jpeg;base64,', rank: 6),
  ];

  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      theme: ThemeData(
        primaryColor: Colors.blue, // Cor principal
        hintColor: Colors.orange, // Cor de destaque
        scaffoldBackgroundColor: Colors.grey[200], // Cor de fundo do scaffold
        cardColor: Colors.white, // Cor dos cards
      ),
      home: Scaffold(
        appBar: AppBar(
          title: Text('Maiores Atletas de Todos os Tempos'),
        ),
        body: ReorderableListView(
          // Método chamado quando um atleta é reorganizado
          onReorder: (int oldIndex, int newIndex) {
            if (oldIndex < newIndex) {
              newIndex -= 1;
            }
            final Athlete athlete = athletes.removeAt(oldIndex);
            athletes.insert(newIndex, athlete);
          },
          // Lista de atletas
          children: [
            for (final athlete in athletes)
              Card(
                key: ValueKey(athlete),
                color: Theme.of(context).cardColor, // Usar a cor definida para os cards
                child: ListTile(
                  leading: CircleAvatar(
                    backgroundImage: NetworkImage(athlete.image), // Carregar imagem da web
                  ),
                  title: Text('${athlete.rank}. ${athlete.name}'),
                ),
              ),
          ],
        ),
        // Botão de ação flutuante para mostrar a lista ranqueada
        floatingActionButton: FloatingActionButton(
          onPressed: () {
            // Ação para mostrar a lista ranqueada
            showDialog(
              context: context,
              builder: (BuildContext context) {
                return AlertDialog(
                  title: Text('Lista de Atletas Ranqueados'),
                  // Lista de atletas
                  content: Column(
                    mainAxisSize: MainAxisSize.min,
                    children: [
                      for (final athlete in athletes)
                        ListTile(
                          leading: CircleAvatar(
                            backgroundImage: NetworkImage(athlete.image), // Carregar imagem da web
                          ),
                          title: Text('${athlete.rank}. ${athlete.name}'),
                        ),
                    ],
                  ),
                  actions: [
                    TextButton(
                      onPressed: () {
                        Navigator.pop(context); // Fechar o diálogo
                      },
                      child: Text('Fechar'),
                    ),
                  ],
                );
              },
            );
          },
          child: Icon(Icons.check),
        ),
      ),
    );
  }
}
